INSERT INTO `cabinrentals` VALUES (7, 'Bear Creek Hideaway', 'http://ical.liverez.com/fetch/A2CD2A03-447A-45DC-A516-E27FF682F21F.ics', NULL, 'Cashes Valley (Blue Ridge)', NULL, NULL, NULL, 'Cabin Rental', NULL, NULL, '4', 160.00, NULL, 176.00, 184.00, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Peak Rate', 'Holiday Rate', NULL, NULL, NULL, 'We no longer offer a $200 security deposit. Instead, we have a Damage Protection Waiver - \"DPW\" is a $45.00 non-refundable damage waiver that is added to every reservation. The DPF will cover up to $750 in accidental or inadvertent damages if, and only if, the damage/accident is reported at that time of occurrence. This is also listed on the Rental Agreement and FAQ. ', 'Holiday Rates will apply to the following holidays: New Yearâ€™s Holiday, Independence Day Holiday, Thanksgiving Holiday, and Christmas Holiday. ', NULL, NULL, NULL, 0.00, NULL, '', NULL, 0.00, 0.00, 110.00, 12.00, 4, 2, 0, NULL, 2.0, '2 queen beds', 'Year Round Creek Frontage', NULL, '<p><b>ATTENTION WATER LOVERS</b>: you have just found paradise! This\r\ncozy authentic log cabin sits right on the banks of Fightingtown Creek, a designated\r\ntrout stream famous in North Georgia!\r\n</p>\r\n<p><b><i>Bear Creek Hideaway</i></b> is the perfect place for your next\r\ngetaway, you\'ll even drive through a small creek approaching the cabin! The main level has a fully equipped kitchen with dishwasher, dining\r\narea and living room with wood burning fireplace and satellite tv. The bedroom on this level has a locally handcrafted queen size log\r\nbed. The bathroom has a new tile shower!\r\n</p>\r\n<p>The loft bedroom has another queen size log bed with a desk\r\narea for those that need to check in at work. The finished basement has an air\r\nhockey table, dartboard and another living room area with satellite tv. </p>\r\n<p>Both the main level and the basement level have lovely porches for you to sit back and relax on while listening to the sounds of\r\nnature. Down by the creek you can enjoy the firepit, picnic table, hammock and\r\nhorseshoe pits.&nbsp; <b>Bear Creek Hideaway</b> is a\r\ngreat place to come and get away from it all!&nbsp;\r\n</p>\r\n<p>\r\n <br />\r\n</p>\r\n<p>\r\n <br />Please note: Fireplaces are available for use from October 1st to April 30th. Outdoor WOOD BURNING fireplaces may be used year round, however MTCR does not provide firewood.&nbsp;\r\n <br />Firepits are also available year round but firewood is not provided in firepits. &nbsp;Outdoor gas log fireplaces may not be used out of season.&nbsp;\r\n</p>\r\n<p></p>', NULL, NULL, 'Blue Ridge', NULL, NULL, NULL, NULL, 'Air Hockey', 'Y', 'Y', 'Y', 'N', 'Y', 'N', 'N', 'Y', 'Y', 'N', 'Y', 'https://my.matterport.com/show/?m=LxJGpgBtHGK', 'Y', 'Y', 'Y', 'N', 'Y', 'N', 'N', 'N', 'Y', 'Y', NULL, 'Y', 34.85685000, -84.42821670, NULL, NULL, 416084, 416039, '<a href=\"http://pinterest.com/pin/create/button/?url=http%3A%2F%2Fwww.mountaintopcabinrentals.com%2FDetailCabin.php%3FPropertyID%3D7&media=http%3A%2F%2Fwww.mountaintopcabinrentals.com%2FCabinRentals%2FBear_Creek_2.jpg&description=Luxury%20Cabin%20Rentals%20in%20Blue%20Ridge%2C%20Georgia.%0Ahttp%3A%2F%2Fwww.mountaintopcabinrentals.com%0A%0A%0A%0A%0A%0A%0A%0A%0A%0A%0A\" class=\"pin-it-button\" count-layout=\"horizontal\"><img border=\"0\" src=\"//assets.pinterest.com/images/PinExt.png\" title=\"Pin It\" /></a>', '<div class=\"fb-like\" data-href=\"http://www.mountaintopcabinrentals.com/DetailCabin.php?PropertyID=7\" data-send=\"false\" data-layout=\"button_count\" data-width=\"450\" data-show-faces=\"false\" data-font=\"arial\"></div>', '<!-- Place this tag where you want the +1 button to render. -->\r\n<div class=\"g-plusone\"></div>\r\n\r\n<!-- Place this tag after the last +1 button tag. -->\r\n<script type=\"text/javascript\">\r\n  (function() {\r\n    var po = document.createElement(\'script\'); po.type = \'text/javascript\'; po.async = true;\r\n    po.src = \'https://apis.google.com/js/plusone.js\'