INSERT INTO `cabinrentals` VALUES (51, 'Creekside Hideaway', 'http://ical.liverez.com/fetch/4857DDD3-300F-4FAB-A347-7343A69E999F.ics', '                                                                                                                                                                                                                                                               ', 'Blue Ridge/Epworth', NULL, NULL, NULL, 'Cabin Rental', NULL, NULL, '4', 185.00, NULL, 203.50, 212.75, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Peak Rate', 'Holiday Rate', NULL, NULL, NULL, 'We no longer offer a $200 security deposit. Instead, we have a Damage Protection Waiver - \"DPW\" is a $45.00 non-refundable damage waiver that is added to every reservation. The DPF will cover up to $750 in accidental or inadvertent damages if, and only if, the damage/accident is reported at that time of occurrence. This is also listed on the Rental Agreement and FAQ. ', 'Peak rates apply on the following holidays: Labor Day Weekend & Memorial Day Weekend, and the entire months of June, July, October and November. ', NULL, NULL, NULL, 0.00, NULL, '', NULL, 0.00, 0.00, 130.00, 12.00, 6, 2, 0, NULL, 2.0, '1 King, 1 Queen, 1 Sleeper Sofa', 'Creek', NULL, '<p>If you are looking for a place to just relax and enjoy the\r\npeace and quiet then <b><i>Creekside Hideaway</i></b> is just the place for you! This cozy\r\ntwo-bedroom cabin is located right on the banks of Fightingtown Creek and\r\noffers all the rest and relaxation you can handle.\r\n</p>\r\n<p>&nbsp;The upper level has\r\none bedroom with a queen bed, one standard bathroom and an open loft with a\r\nliving area that has a television with DVD player and cable access and a sleeper\r\nsofa. </p>\r\n<p>The main level features one bedroom with a king bed and\r\ntelevision with a DVD player and cable access, one standard bathroom, a fully\r\nequipped kitchen and dining area and a living area with leather furniture, a\r\nlarge wood burning fireplace and flat panel television with cable access and\r\nDVD/VCR.</p>\r\n<p>The lower level has a foosball table, ping pong table and\r\ntelevision, making this area perfect for the kids and access leading to the\r\nlower patio where the bubbling hot tub awaits you! </p>\r\n<p><i><b>Creekside Hideaway </b></i>also has a covered porch with a table and\r\nchairs, a large charcoal grill and steps leading down to the covered deck that\r\noverlooks the cool, running waters of Fightingtown Creek! Feel free to bring\r\nyour rod and reel and try your luck at catching some of the delicious native\r\ntrout. Whether you are a couple wanting a romantic weekend or a family looking\r\nfor some quality time, <b><i>Creekside Hideaway</i></b> is just the place.\r\n</p>\r\n<p>\r\n <br />\r\n</p>\r\n<p>\r\n <br />\r\n</p>\r\n<p>\r\n <br />Please note: Fireplaces are available for use from October 1st to April 30th. Outdoor WOOD BURNING fireplaces may be used year round, however MTCR does not provide firewood.&nbsp;&nbsp;\r\n <br />Firepits are also available year round but firewood is not provided in firepits. &nbsp;Outdoor gas log fireplaces may not be used out of season.&nbsp;\r\n</p>\r\n<p></p>', NULL, 'Yes', 'Blue Ridge', NULL, NULL, NULL, NULL, 'Foosball Table, Ping Pong Table', 'Y', 'Y', 'Y', 'N', 'Y', 'Y', 'N', 'N', 'Y', 'N', 'N', 'https://my.matterport.com/show/?m=3cGTFvPrUCF', 'Y', 'N', 'Y', 'N', 'N', 'N', 'N', 'N', 'Y', 'N', NULL, 'Y', 34.87668330, -84.39430000, NULL, NULL, 311364, 311341, '<a href=\"http://pinterest--com--rztrkr--com.rztrkr.com/pin/create/button/?url=http%3A%2F%2Fwww.mountaintopcabinrentals.com%2FDetailCabin.php%3FPropertyID%3D51&media=http%3A%2F%2Fwww.mountaintopcabinrentals.com%2FCabinRentals%2Fcreekside_3.jpg&description=Cabin%20Rentals%20in%20Blue%20Ridge%2C%20Georgia.%0Awww.mountaintopcabinrentals.com%0A%0A%0A\" class=\"pin-it-button\" count-layout=\"horizontal\"><img border=\"0\" src=\"//assets.pinterest.com/images/PinExt.png\" title=\"Pin It\